    `,
    'package.json': JSON.stringify({jest: {testEnvironment: 'node'}}),
  });

  const result = runJest(DIR, ['-i', '--forceExit', './foo/bar.js']);

  expect(result.exitCode).toBe(1);
  expect(result.stdout).toMatch(/No tests found([\S\s]*)2 files checked./);
