              },
              type: 'UserStats',
            },
          },
          type: 'Body',
        },
      ],
      type: 'main',
    };
    const indented = formatLines2(val);

    expect(dedentLines(indented)).toBeNull();
  });

  test('markup text multi-line', () => {
    const text = [
      'for (key in foo) {',
      '  if (Object.prototype.hasOwnProperty.call(foo, key)) {',
      '    doSomething(key);',
      '  }',
      '}',
