async function leakMemory() {
  console.log(
    `Intentionally leaking memory: ${(
      process.memoryUsage().heapUsed /
      1024 /
      1024
    ).toFixed(2)}MB at start`,
  );

  let i = Number.MAX_SAFE_INTEGER / 2;
  while (true) {
    i++;

    leakStore.push(i);
  }
}

function fatalExitCode() {
  process.exit(134);
}

function safeFunction() {
  // Doesn't do anything.
}

module.exports = {
  fatalExitCode,
  leakMemory,
