function getGlobal(): typeof globalThis {
  return runInContext('this', createContext()) as typeof globalThis;
}

beforeEach(() => {
  installCommonGlobals = (
    require('../installCommonGlobals') as typeof import('../installCommonGlobals')
  ).default;
