    'should be reflexive',
    ([a, b]) => {
      // Given: a and b identical values
      expect(a).toEqual(b);
    },
  );

  const safeExpectEqual = (a: unknown, b: unknown) => {
    try {
      expect(a).toEqual(b);
      return true;
    } catch {
      return false;
    }
  };

  it.prop([fc.anything(anythingSettings), fc.anything(anythingSettings)], {
    ...assertSettings,
