    expect(() =>
      jestExpect(b).toMatchObject(matcher),
    ).toThrowErrorMatchingSnapshot();
  });

  it('toMatchObject ignores symbol key properties', () => {
    // issue 13638
    const sym = Symbol('foo');
    const sym2 = Symbol('foo2');
    jestExpect({}).not.toMatchObject({[sym]: true});
