    ${[]}                       | ${[]}
    ${[{foo: {bar: 'baz'}}]}    | ${[{foo: {bar: 'baz'}}]}
    ${Number.POSITIVE_INFINITY} | ${Number.POSITIVE_INFINITY}
    ${Number.NEGATIVE_INFINITY} | ${Number.NEGATIVE_INFINITY}
    ${Number.NaN}               | ${Number.NaN}
  `('$left == $right', ({left, right}) => {
    expect(left).toEqual(right);
