  ]);

  process.emit(
    'message',
    [
      CHILD_MESSAGE_CALL,
      true, // Not really used here, but for type purity.
      'fooThrowsAnErrorWithExtraProperties',
      [],
    ],
    null,
  );

  expect(spyProcessSend.mock.calls[3][0]).toEqual([
    PARENT_MESSAGE_CLIENT_ERROR,
    'MockExtendedError',
