jest.setTimeout(15_000);

jest.mock('graceful-fs', () => {
  const realFs = jest.requireActual<typeof import('fs')>('fs');

  const mockedFs: typeof import('fs') = {
    ...realFs,
    statSync: path => {
      if (path === '/foo/bar/prefix') {
