    jestExpect(fn).not.toHaveReturnedTimes(2);

    expect(() =>
      jestExpect(fn).toHaveReturnedTimes(2),
    ).toThrowErrorMatchingSnapshot();
  });

  test('calls that throw are not counted', () => {
