    try {
      fn(true);
    } catch {
      // ignore error
    }

    fn(false);

    jestExpect(fn).toHaveReturnedTimes(2);

    expect(() =>
      jestExpect(fn).not.toHaveReturnedTimes(2),
    ).toThrowErrorMatchingSnapshot();
  });

  test('includes the custom mock name in the error message', () => {
    const fn = jest.fn(() => 42).mockName('named-mock');
