'use strict';

var events = require('../../').Runner.constants;
var helpers = require('./helpers');
var reporters = require('../../').reporters;

var JSONStream = reporters.JSONStream;
var createMockRunner = helpers.createMockRunner;
var makeExpectedTest = helpers.makeExpectedTest;
var makeRunReporter = helpers.createRunReporterFunction;

var EVENT_RUN_BEGIN = events.EVENT_RUN_BEGIN;
var EVENT_RUN_END = events.EVENT_RUN_END;
var EVENT_TEST_FAIL = events.EVENT_TEST_FAIL;
var EVENT_TEST_PASS = events.EVENT_TEST_PASS;

describe('JSON Stream reporter', function () {
  var runReporter = makeRunReporter(JSONStream);
  var expectedTitle = 'some title';
  var expectedFullTitle = 'full title';
  var expectedFile = 'someTest.spec.js';
  var expectedDuration = 1000;
  var currentRetry = 1;
  var expectedSpeed = 'fast';
  var expectedTest = makeExpectedTest(
    expectedTitle,
    expectedFullTitle,
    expectedFile,
    expectedDuration,
    currentRetry,
    expectedSpeed
  );
  var expectedErrorMessage = 'error message';
  var expectedErrorStack = 'error stack';
  var expectedError = {
    message: expectedErrorMessage
  };

  describe('event handlers', function () {
    describe("on 'start' event", function () {
      it('should write stringified start with expected total', function () {
        var runner = createMockRunner('start', EVENT_RUN_BEGIN);
        var expectedTotal = 12;
        runner.total = expectedTotal;
        var options = {};
        var stdout = runReporter({}, runner, options);

        expect(
          stdout[0],
          'to equal',
          '["start",{"total":' + expectedTotal + '}]\n'
        );
      });
    });

    describe("on 'pass' event", function () {
      it('should write stringified test data', function () {
        var runner = createMockRunner(
          'pass',
          EVENT_TEST_PASS,
          null,
          null,
          expectedTest
        );
        var options = {};
        var stdout = runReporter({}, runner, options);

        expect(
          stdout[0],
          'to equal',
          '["pass",{"title":' +
            `"${expectedTitle}"` +
            ',"fullTitle":' +
            `"${expectedFullTitle}"` +
            ',"file":' +
            `"${expectedFile}"` +
            ',"duration":' +
            expectedDuration +
            ',"currentRetry":' +
            currentRetry +
            ',"speed":' +
            `"${expectedSpeed}"` +
            '}]\n'
        );
      });
    });

    describe("on 'fail' event", function () {
      describe('when error stack exists', function () {
        it('should write stringified test data with error data', function () {
          expectedError.stack = expectedErrorStack;
          var runner = createMockRunner(
            'fail two args',
            EVENT_TEST_FAIL,
            null,
            null,
            expectedTest,
            expectedError
          );
          var options = {};
          var stdout = runReporter({}, runner, options);

          expect(
            stdout[0],
            'to equal',
            '["fail",{"title":' +
              `"${expectedTitle}"` +
              ',"fullTitle":' +
              `"${expectedFullTitle}"` +
              ',"file":' +
              `"${expectedFile}"` +
              ',"duration":' +
              expectedDuration +
              ',"currentRetry":' +
              currentRetry +
              ',"speed":' +
              `"${expectedSpeed}"` +
              ',"err":' +
              `"${expectedErrorMessage}"` +
              ',"stack":' +
              `"${expectedErrorStack}"` +
              '}]\n'
          );
        });
      });

      describe('when error stack does not exist', function () {
        it('should write stringified test data with error data', function () {
          expectedError.stack = null;
          var runner = createMockRunner(
            'fail two args',
            EVENT_TEST_FAIL,
            null,
            null,
            expectedTest,
            expectedError
          );
          var options = {};
          var stdout = runReporter(this, runner, options);

          expect(
            stdout[0],
            'to equal',
            '["fail",{"title":' +
              `"${expectedTitle}"` +
              ',"fullTitle":' +
              `"${expectedFullTitle}"` +
              ',"file":' +
              `"${expectedFile}"` +
              ',"duration":' +
              expectedDuration +
              ',"currentRetry":' +
              currentRetry +
              ',"speed":' +
              `"${expectedSpeed}"` +
              ',"err":' +
              `"${expectedErrorMessage}"` +
              ',"stack":null}]\n'
          );
        });
      });
    });

    describe("on 'end' event", function () {
      it('should write summary statistics', function () {
        var runner = createMockRunner('end', EVENT_RUN_END);
        var options = {};
        var stdout = runReporter(this, runner, options);
        expect(stdout[0], 'to match', /end/);
      });
    });
  });
});

'use strict';

var fs = require('node:fs');
var sinon = require('sinon');
var JSONReporter = require('../../lib/reporters/json');
var utils = require('../../lib/utils');
var Mocha = require('../../');
var Suite = Mocha.Suite;
var Runner = Mocha.Runner;
var Test = Mocha.Test;

describe('JSON reporter', function () {
  var mocha;
  var suite;
  var runner;
  var testTitle = 'json test 1';
  var testFile = 'someTest.spec.js';
  var noop = function () {};

  beforeEach(function () {
    mocha = new Mocha({
      reporter: 'json'
    });
    suite = new Suite('JSON suite', 'root');
    runner = new Runner(suite);
  });

  afterEach(function () {
    sinon.restore();
  });

  describe('test results', function () {
    beforeEach(function () {
      var options = {};
      /* eslint no-unused-vars: off */
      var mochaReporter = new mocha._reporter(runner, options);
    });

    beforeEach(function () {
      sinon.stub(process.stdout, 'write').callsFake(noop);
    });

    it('should have 1 test failure', function (done) {
      var error = {message: 'oh shit'};

      var test = new Test(testTitle, function (done) {
        done(new Error(error.message));
      });

      test.file = testFile;
      suite.addTest(test);

      runner.run(function (failureCount) {
        sinon.restore();
        expect(runner, 'to satisfy', {
          testResults: {
            failures: [
              {
                title: testTitle,
                file: testFile,
                err: {
                  message: error.message
                }
              }
            ]
          }
        });
        expect(failureCount, 'to be', 1);
        done();
      });
    });

    it('should have 1 test pending', function (done) {
      var test = new Test(testTitle);
      test.file = testFile;
      suite.addTest(test);

      runner.run(function (failureCount) {
        sinon.restore();
        expect(runner, 'to satisfy', {
          testResults: {
            pending: [
              {
                title: testTitle,
                file: testFile
              }
            ]
          }
        });
        expect(failureCount, 'to be', 0);
        done();
      });
    });

    it('should have 1 test pass', function (done) {
      const test = new Test(testTitle, () => {});

      test.file = testFile;
      suite.addTest(test);

      runner.run(function (failureCount) {
        sinon.restore();
        expect(runner, 'to satisfy', {
          testResults: {
            passes: [
              {
                title: testTitle,
                file: testFile,
                speed: /(slow|medium|fast)/
              }
            ]
          }
        });
        expect(failureCount, 'to be', 0);
        done();
      });
    });

    it('should handle circular objects in errors', function (done) {
      var testTitle = 'json test 1';
      function CircleError() {
        this.message = 'oh shit';
        this.circular = this;
      }
      var error = new CircleError();

      var test = new Test(testTitle, function (done) {
        throw error;
      });

      test.file = testFile;
      suite.addTest(test);

      runner.run(function (failureCount) {
        sinon.restore();
        expect(runner, 'to satisfy', {
          testResults: {
            failures: [
              {
                title: testTitle,
                file: testFile,
                err: {
                  message: error.message
                }
              }
            ]
          }
        });
        expect(failureCount, 'to be', 1);
        done();
      });
    });
  });

  describe('when "reporterOption.output" is provided', function () {
    var expectedDirName = 'reports';
    var expectedFileName = 'reports/test-results.json';
    var options = {
      reporterOption: {
        output: expectedFileName
      }
    };

    beforeEach(function () {
      /* eslint no-unused-vars: off */
      var mochaReporter = new mocha._reporter(runner, options);
    });

    beforeEach(function () {
      // Add one test to suite to avoid assertions against empty test results
      var test = new Test(testTitle, () => {});
      test.file = testFile;
      suite.addTest(test);
    });

    it('should write test results to file', function (done) {
      const fsMkdirSync = sinon.stub(fs, 'mkdirSync');
      const fsWriteFileSync = sinon.stub(fs, 'writeFileSync');

      fsWriteFileSync.callsFake(function (filename, content) {
        const expectedJson = JSON.stringify(runner.testResults, null, 2);
        expect(expectedFileName, 'to be', filename);
        expect(content, 'to be', expectedJson);
      });

      runner.run(function () {
        expect(
          fsMkdirSync.calledWith(expectedDirName, {recursive: true}),
          'to be true'
        );
        expect(fsWriteFileSync.calledOnce, 'to be true');
        done();
      });
    });

    it('should warn and write test results to console', function (done) {
      const fsMkdirSync = sinon.stub(fs, 'mkdirSync');
      const fsWriteFileSync = sinon.stub(fs, 'writeFileSync');

      fsWriteFileSync.throws('unable to write file');

      const outLog = [];
      const fake = chunk => outLog.push(chunk);
      sinon.stub(process.stderr, 'write').callsFake(fake);
      sinon.stub(process.stdout, 'write').callsFake(fake);

      runner.run(function () {
        sinon.restore();
        expect(
          fsMkdirSync.calledWith(expectedDirName, {recursive: true}),
          'to be true'
        );
        expect(fsWriteFileSync.calledOnce, 'to be true');
        expect(
          outLog[0],
          'to contain',
          `[mocha] writing output to "${expectedFileName}" failed:`
        );
        expect(outLog[1], 'to match', /"fullTitle": "JSON suite json test 1"/);
        done();
      });
    });

    it('should throw "unsupported error" in browser', function () {
      sinon.stub(utils, 'isBrowser').callsFake(() => true);
      expect(
        () => new JSONReporter(runner, options),
        'to throw',
        'file output not supported in browser'
      );
    });
  });
});

