var EVENT_TEST_PASS = events.EVENT_TEST_PASS;

describe('Markdown reporter', function () {
  var runReporter = makeRunReporter(Markdown);
  var expectedTitle = 'expected title';
  var expectedFullTitle = 'full title';
  var sluggedFullTitle = 'full-title';
  var noop = function () {};

  describe('event handlers', function () {
    describe("on 'suite' event", function () {
      it("should write expected slugged titles on 'end' event", function () {
        var expectedSuite = {
          title: expectedTitle,
          fullTitle: function () {
            return expectedFullTitle;
          },
          suites: [
            {
              title: expectedTitle,
              fullTitle: function () {
                return expectedFullTitle;
              },
              suites: []
            }
          ]
        };
        var runner = createMockRunner(
          'suite suite end',
          EVENT_SUITE_BEGIN,
          EVENT_SUITE_END,
          EVENT_RUN_END,
          expectedSuite
        );
        runner.suite = expectedSuite;
        var options = {};
        var stdout = runReporter({}, runner, options);

        var expectedArray = [
          '# TOC\n',
          ' - [' +
            expectedTitle +
            '](#' +
            sluggedFullTitle +
            ')\n   - [' +
            expectedTitle +
            '](#' +
            sluggedFullTitle +
            ')\n',
          '<a name="' + sluggedFullTitle + '"></a>\n ' + expectedTitle + '\n'
        ];

        expect(stdout, 'to equal', expectedArray);
      });
    });

    describe("on 'pass' event", function () {
      it("should write test code inside js code block, on 'end' event", function () {
        var expectedSuite = {
          title: expectedTitle,
          fullTitle: function () {
            return expectedFullTitle;
          },
          suites: []
        };
        var expectedDuration = 1000;
        var currentRetry = 1;
        var expectedBody = 'some body';
        var expectedTest = {
          title: expectedTitle,
          fullTitle: function () {
            return expectedFullTitle;
          },
          duration: expectedDuration,
          currentRetry: function () {
            return currentRetry;
          },
          slow: noop,
          body: expectedBody
        };
        var runner = createMockRunner(
          'pass end',
          EVENT_TEST_PASS,
          EVENT_RUN_END,
          null,
          expectedTest
        );
        runner.suite = expectedSuite;
        var options = {};
        var stdout = runReporter({}, runner, options);

        var expectedArray = [
          '# TOC\n',
          ' - [' + expectedTitle + '](#' + sluggedFullTitle + ')\n',
          expectedTitle + '.\n\n```js\n' + expectedBody + '\n```\n\n'
        ];

        expect(stdout, 'to equal', expectedArray);
      });
    });
  });
});

'use strict';

var sinon = require('sinon');
var events = require('../../').Runner.constants;
var helpers = require('./helpers');
var reporters = require('../../').reporters;

var Min = reporters.Min;
var createMockRunner = helpers.createMockRunner;
var makeRunReporter = helpers.createRunReporterFunction;

var EVENT_RUN_BEGIN = events.EVENT_RUN_BEGIN;
var EVENT_RUN_END = events.EVENT_RUN_END;

describe('Min reporter', function () {
  var runReporter = makeRunReporter(Min);
  var noop = function () {};

  describe('event handlers', function () {
    describe("on 'start' event", function () {
      it('should clear screen then set cursor position', function () {
        var runner = createMockRunner('start', EVENT_RUN_BEGIN);
        var options = {};
        var fakeThis = {
          epilogue: noop
        };
        var stdout = runReporter(fakeThis, runner, options);

        var expectedArray = ['\u001b[2J', '\u001b[1;3H'];
        expect(stdout, 'to equal', expectedArray);
      });
    });

    describe("on 'end' event", function () {
      it('should call epilogue', function () {
        var fakeThis = {
          epilogue: sinon.stub().callsFake(noop)
        };
        var runner = createMockRunner('end', EVENT_RUN_END);
        var options = {};
        runReporter(fakeThis, runner, options);

        expect(fakeThis.epilogue.called, 'to be true');
      });
    });
  });
});

'use strict';

const {
  types,
  expectedTypeForFlag
} = require('../../../lib/cli/run-option-metadata');

describe('mocha-flags', function () {
  describe('expectedTypeForFlag()', function () {
    Object.entries(types).forEach(([dataType, flags]) => {
      flags.forEach(flag => {
        it(`returns expected ${flag}'s type as ${dataType}`, function () {
          expect(expectedTypeForFlag(flag), 'to equal', dataType);
        });
      });
    });

    it('returns undefined for node flags', function () {
      expect(expectedTypeForFlag('--throw-deprecation'), 'to equal', undefined);
      expect(expectedTypeForFlag('throw-deprecation'), 'to equal', undefined);
    });

    it('returns undefined for unsupported flags', function () {
      expect(expectedTypeForFlag('--foo'), 'to equal', undefined);
    });
  });
});

