    it('reruns test when file matching --watch-files is removed', function () {
      const testFile = path.join(tempDir, 'test.js');
      copyFixture(DEFAULT_FIXTURE, testFile);

      const watchedFile = path.join(tempDir, 'lib/file.xyz');
